import { createSlice, configureStore } from "@reduxjs/toolkit";

let intialValuesItems = {
  cartItem: [],
  toggleCart: false,
};
let cartSlice = createSlice({
  name: "cart",
  initialState: intialValuesItems,
  reducers: {
    addItem(state, action) {
      if (state.cartItem.length == 0) state.cartItem.push(action.payload);
      else {
        state.cartItem.map((values) => {
          return values.id == action.payload.id;
        });
      }

      if (state.cartItem) console.log(action);
    },
    removeItems(state) {},
    toggleCart(state) {
      console.log(state.toggleCart);
      state.toggleCart = true;
    },
  },
});

let storeCart = configureStore({ reducer: { cartItem: cartSlice.reducer } });

export let cartActions = cartSlice.actions;
export default storeCart;
