import { createSlice, configureStore } from "@reduxjs/toolkit";

let intialValuesItems = {
  cartItemList: [],
  toggleCart: false,
};
let cartSlice = createSlice({
  name: "cart",
  initialState: intialValuesItems,
  reducers: {
    addItem(state, action) {
      if (state.cartItemList.length == 0) {
        console.log(state.cartItemList);
        state.cartItemList.push(action.payload);
      } else {
        state.cartItemList.map((values) => {
          if (values.id === action.payload.id) {
            console.log(state.cartItemList);
            let increasecount = values.count + 1;
            let newObject = {
              ...values,
              count: values.count + 1,
            };
            console.log(state.cartItemList);
          } else {
            state.cartItemList.push(action.payload);
          }
        });
        // console.log(cartitesmlist);
        // if (cartitesmlist.length > 0) state.cartItemList = state.cartItemList;
        // else state.cartItemList = state.cartItemList.push(action.payload);
      }

      console.log(state.cartItemList);
    },
    removeItems(state) {},
    toggleCart(state) {
      console.log(state.toggleCart);
      state.toggleCart = true;
    },
  },
});

let storeCart = configureStore({ reducer: { cartItem: cartSlice.reducer } });

export let cartActions = cartSlice.actions;
export default storeCart;
