import { createSlice, configureStore } from "@reduxjs/toolkit";

let intialValuesItems = {
  cartItemList: [],
  toggleCart: false,
};
let cartSlice = createSlice({
  name: "cart",
  initialState: intialValuesItems,
  reducers: {
    addItem(state, action) {
      if (state.cartItemList.length == 0) {
        console.log(state.cartItemList);
        state.cartItemList.push(action.payload);
      } else {
        let allListWithOutCurrentObject = state.cartItemList.filter(
          (values) => !(values.id === action.payload.id)
        );
        if (allListWithOutCurrentObject.length == 0)
          state.cartItemList.push(allListWithOutCurrentObject);

        let currentObject = state.cartItemList.find(
          (values) => values.id == action.payload.id
        );

        console.log(currentObject.id);
        // let newObject = {
        //   ...currentObject,
        //   count: Number(currentObject.count) + 1,
        // };
        // console.log(newObject);
        // state.cartItemList.push(newObject);
      }

      console.log(state.cartItemList);
    },
    removeItems(state) {},
    toggleCart(state) {
      console.log(state.toggleCart);
      state.toggleCart = true;
    },
  },
});

let storeCart = configureStore({ reducer: { cartItem: cartSlice.reducer } });

export let cartActions = cartSlice.actions;
export default storeCart;
