import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useDispatch, useSelector } from "react-redux";
import { Fragment, useEffect } from "react";
import Notification from "./components/UI/Notification";
import storeCart from "./store";
import { cartActions } from "./store";

let intialState = true;
function App() {
  let toggleCart = useSelector((value) => value.cartItem.toggleCart);
  let cartItems = useSelector((data) => data.cartItem.items);

  let notificationData = useSelector((data) => data.cartItem.notification);

  let dispatch = useDispatch();

  useEffect(() => {
    async function submitDetails() {
      dispatch(
        cartActions.notificationFucntion({
          status: "Pending",
          title: "Pending..",
          message: "Sending the Data Request",
        })
      );
      let response = await fetch(
        "https://learningreact-f5a3c-default-rtdb.firebaseio.com/cartItems.json",
        {
          method: "PUT",
          body: JSON.stringify(cartItems),
        }
      );

      if (!response.ok) {
        throw new Error("Failed to Load the request");
      }

      dispatch(
        cartActions.notificationFucntion({
          status: "success",
          title: "Sucess",
          message: "Cart Items Details Sucessfully",
        })
      );
    }
    if (!intialState) {
      intialState = false;
      submitDetails().catch(() => {
        dispatch(
          cartActions.notificationFucntion({
            status: "error",
            title: "Failed",
            message: "Failed to add the deatils ",
          })
        );
      });
    }
  }, [cartItems, dispatch]);

  return (
    <Fragment>
      {notificationData && (
        <Notification
          status={notificationData.status}
          title={notificationData.title}
          message={notificationData.message}
        ></Notification>
      )}
      <Layout>
        {toggleCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
